const ImageItem = ({ image, toggleFavorite, isFavorited }) => {
  const favorited = isFavorited(image.id);

  return (
    <div className="relative group rounded-lg overflow-hidden shadow-md hover:shadow-lg transition-shadow">
      <img
        src={image.urls.small}
        alt={image.alt_description || "Image"}
        className="w-full h-48 object-cover"
      />
      <button
        onClick={() => toggleFavorite(image)}
        className="absolute top-2 right-2 bg-white rounded-full p-2 shadow-md hover:bg-gray-100 transition-colors"
        aria-label="Add to favorites"
      >
        <span
          className={`text-xl ${favorited ? "text-red-500" : "text-gray-400"}`}
        >
          {favorited ? "♥" : "♡"}
        </span>
      </button>
    </div>
  );
};

export default ImageItem;
