import { useState } from "react";

const SearchBar = (props) => {
  const { onSubmit } = props; // be consistent with the name
  const [term, setTerm] = useState("");

  const handleChange = (event) => {
    setTerm(event.target.value);
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    // form submission refresh page and clear inputs
    onSubmit(term);
  };
  return (
    <div>
      {/* By wrapping our inputs in a form tag, we get default browser behavior
    // this means on Submit is exposed to both a submit button click
    // Or the user hitting the enter key*/}
      <form
        className="flex bg-slate-200 p-2 px-4 justify-center rounded-full"
        onSubmit={handleFormSubmit}
      >
        <input
          className="flex text-xs bg-slate-200 rounded-full"
          type="text"
          placeholder="Search for images..."
          onChange={handleChange}
          value={term}
        />
        <button
          type="submit"
          className="px-6 py-2 bg-black text-white rounded-full hover:bg-slate-600"
        >
          Search
        </button>
      </form>
    </div>
  );
};

export default SearchBar;
