import "./App.css";
import { useState } from "react";
import { Routes, Route, BrowserRouter } from "react-router-dom";
import SearchPage from "./pages/SearchPage";
import FavoritesPage from "./pages/FavoritesPage";
import Navbar from "./components/NavBar";

function App() {
  // State for favorites - passed down to child components
  const [favorites, setFavorites] = useState([]);

  // Function to add/remove from favorites
  const toggleFavorite = (image) => {
    setFavorites((prevFavorites) => {
      const isFavorited = prevFavorites.some((fav) => fav.id === image.id);
      if (isFavorited) {
        // Remove from favorites
        return prevFavorites.filter((fav) => fav.id !== image.id);
      } else {
        // Add to favorites
        return [...prevFavorites, image];
      }
    });
  };

  // Check if an image is favorited
  const isFavorited = (imageId) => {
    return favorites.some((fav) => fav.id === imageId);
  };

  return (
    <div className="container mx-auto grid grid-cols-6 gap-4 mt-4">
      <Navbar />
      <div className="col-span-5">
        <Routes>
          <Route path="/" element={<SearchPage />}></Route>
          <Route path="/favorites" element={<FavoritesPage />}></Route>
        </Routes>
      </div>
    </div>
  );
}

export default App;
